partials.components_anchored_menu:
{{#with (default menuId (concat "menu-" (random 1000 9999))) as |menuId|}}
<div class="mdc-menu-surface--anchor {{../anchorClasses}}">
  <button class="mdc-button {{../buttonClasses}}" id="{{menuId}}-trigger">
    {{#if ../imageUrl ~}}
      <img src="{{../imageUrl}}" class="{{../imageClasses}}" />
    {{/if}}
    {{#if ../iconClasses}}
      <span class="{{../iconClasses}}"></span>
    {{/if ~}}
    {{{../buttonLabel}}}
    {{#if ../arrowClasses}}
      <span class="{{../arrowClasses}}"></span>
    {{/if}}
  </button>
  <div class="mdc-menu mdc-menu-surface {{../menuClasses}}" tabindex="-1" id="{{menuId}}" data-menu-trigger="#{{menuId}}-trigger">
    <ul class="mdc-menu__items mdc-list {{../menuListClasses}}" role="menu" aria-hidden="true">
      {{> @partial-block}}
    </ul>
  </div>
</div>
{{/with}}

partials.components_horizontal_card_scroller:
{{#ifAll (compare (length items) "===" 0) @partial-block}}
  {{> @partial-block}}
{{else}}
  <div class="mdc-layout-grid equal-height {{outerClasses}}">
    {{#if (any items)}}
    <div class="scroll-button scroll-left {{scrollButtonClasses}} {{scrollButtonLeftClasses}}">
      <span class="scroll-icon {{scrollIconClasses}} {{scrollLeftIconClasses}}"></span>
    </div>
    <div class="scroll-button scroll-right {{scrollButtonClasses}} {{scrollButtonRightClasses}}">
      <span class="scroll-icon {{scrollIconClasses}} {{scrollRightIconClasses}}"></span>
    </div>
    {{/if}}
    <div class="mdc-layout-grid__inner scrolling-grid {{innerClasses}}">
      {{#if (any items)}}
        {{#each items}}
          <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone {{../itemClasses}}">
            {{> (lookup ../this 'itemsPartial')}}
          </div>
        {{/each}}
        {{#if moreUrl}}
          <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone latest-content-primary last-card {{moreClasses}}">
            <div class="mdc-card align-centre mdc-typography--body1 {{moreCardClasses}}">
              <a href="{{{moreUrl}}}">{{default moreText "More"}}</a>
            </div>
          </div>
        {{/if}}
      {{/if}}
    </div>
  </div>
{{/ifAll}}

templates.head-tags:
{{#compare request.params.path "===" "signatories" ~}}
<title>{{route.metaData.title}}</title>
<meta property="og:title" content="{{route.metaData.title}}" data-dynamic="true">
{{else}}
{{#if data.primaryResponse.hits.hits.[0] ~}}
{{#with data.primaryResponse.hits.hits.[0]._source ~}}
{{#if title ~}}
<title>{{{title}}} is a signatory of the Scotland Declaration on Human Rights</title>
<meta property="og:title" content="{{{title}}} is a signatory of the Scotland Declaration on Human Rights" data-dynamic="true">
{{else}}
<title>{{route.metaData.title}}</title>
<meta property="og:title" content="{{route.metaData.title}}" data-dynamic="true">
{{/if ~}}
{{#if files.0.uploadUrl ~}}
<meta property="og:image" content="{{files.0.uploadUrl}}" data-dynamic="true">
<meta property="og:image:width" content="128" data-dynamic="true">
<meta property="og:image:height" content="128" data-dynamic="true">
{{/if ~}}
{{#ifAny og_summary description ../og_summary ../description ~}}
<meta property="og:summary" content="{{stripTags (regexReplace (default og_summary (default ../og_summary (default description ../description))) '\r|\n' 'gi' '')}}" data-dynamic="true">
<meta name="description" content="{{stripTags (regexReplace (default description (default ../description (default og_summary ../og_summary))) '\r|\n' 'gi' '')}}" data-dynamic="true">
{{/ifAny ~}}
{{/with ~}}
{{else}}
<title>{{route.metaData.title}}</title>
<meta property="og:title" content="{{route.metaData.title}}" data-dynamic="true">
{{/if ~}}
{{/compare ~}}

queries.signatory-search:
{
  "size": 16,
  "from": {{multiply (subtract (default request.params.query.page 1) 1) 16}},
  "_source": [
    "slug",
    "title",
    "rendered.humanrightsdeclaration_search_result"
  ],
  "query": {
    "bool": {
      {{#if request.params.query.q ~}}
      "should": [
        {
          "query_string": {
            "query": {{{stringify (default request.params.query.q "")}}},
            "default_field": "text_bag",
            "default_operator": "AND",
            "analyzer": "snowball"
          }
        },
        {
          "match_phrase": {
            "title": {
              "query": {{{stringify (default request.params.query.q "")}}},
              "boost": 10
            }
          }
        }
      ]
      {{else}}
      "must": [
        {
          "match_all": {}
        }
      ]
      {{/if ~}}
    }
  },
  "sort": {
    {{#if request.params.query.sort ~}}
    "slug": "asc"
    {{else}}
    {{#if request.params.query.q ~}}
    "_score": "desc"
    {{else}}
    "date_signed": "desc"
    {{/if ~}}
    {{/if ~}}
  }
}
